import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addToCart, removeFromCart } from '../redux/cartSlice';

export default function MenuItem({ item }) {
  const dispatch = useDispatch();
  const cartItem = useSelector(state =>
    state.cart.items.find(cart => cart.id === item.id)
  );

  return (
    <div className="bg-white border rounded-lg p-4 shadow hover:shadow-lg transition">
      <div className="aspect-w-4 aspect-h-3 mb-4 overflow-hidden rounded-md">
        <img
          src={item.image}
          alt={item.name}
          className="object-cover w-full h-full"
        />
      </div>

      <h3 className="text-xl font-semibold mb-1">{item.name}</h3>
      <p className="text-gray-600 text-sm mb-2">{item.description}</p>
      <p className="text-gray-800 font-medium mb-3">${item.price.toFixed(2)}</p>

      {cartItem ? (
        <div className="flex items-center gap-2">
          <button
            onClick={() => dispatch(removeFromCart(item.id))}
            className="px-3 py-1 bg-red-500 text-white rounded"
          >
            -
          </button>
          <span>{cartItem.quantity}</span>
          <button
            onClick={() => dispatch(addToCart(item))}
            className="px-3 py-1 bg-green-500 text-white rounded"
          >
            +
          </button>
        </div>
      ) : (
        <button
          onClick={() => dispatch(addToCart(item))}
          className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
        >
          Add to Cart
        </button>
      )}
    </div>
  );
}
